import React, { useState } from "react";
import axios from "axios";
import { Form, Button, Alert, Container } from "react-bootstrap";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleUserName = (event) => {
    console.log(event.target)
    setUsername(event.target)
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    var loginInfo = {
        "username": username,
        "password": password
    }
    console.log(loginInfo)
    axios.get("/login", JSON.stringify(loginInfo))
              .then(response => console.log(response.data))
  };

  return (
    <Container>
        <Form>
        <Form.Group className="mb-3" controlId="formBasicUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control type="username" placeholder="Enter username" onChange={handleUserName}/>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" onChange={setPassword}/>
        </Form.Group>
        <Button variant="primary" type="submit" onClick = {handleSubmit}>
            Login
        </Button>
        </Form>
    </Container>
  );
};

export default Login;
