import React, { useRef , useState} from 'react';
import axios from "axios";
import { Editor } from '@tinymce/tinymce-react';
import Card from "react-bootstrap/Card"

// google api custom search json api key AIzaSyA4yWgqa2eDcBvv5StSDs_AbVeaZ8eGaCc

// cx number
/* <script async src="https://cse.google.com/cse.js?cx=969e5dfcbcafd41b8">
</script>
<div class="gcse-search"></div> */

export default function TextBox() {
  const editorRef = useRef(null);

  const [cardContent, setCardContent] = useState("");

  return (
    <>
      <Editor
        apiKey='7c0izecivr1lps70wenvvlpyffvpawkgcjjy7t0h7rlfjod4'
        onInit={(evt, editor) => editorRef.current = editor}
        initialValue="<p>This is the initial content of the editor.</p>"
        init={{
          height: 500,
          menubar: false,
          plugins: [
            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
          ],
          toolbar: 'undo redo | blocks | ' +
            'bold italic forecolor | alignleft aligncenter ' +
            'alignright alignjustify | bullist numlist outdent indent | ' +
            'removeformat | help | myCustomButton',
          setup: editor => {
            editor.ui.registry.addButton('myCustomButton', {
            text: 'Search',
            onAction: async () => {
              var search_content = editor.selection.getContent();
              console.log(search_content)
              console.log(typeof(search_content))

              axios.post("/search", {"search": search_content})
              .then(response => {
                console.log(response.data)
                setCardContent(response.data.answer);

              })

              var googleApi = ""
              var cx = "969e5dfcbcafd41b8"
              var googleURL = "https://www.googleapis.com/customsearch/v1?key=" + googleApi + "&cx=" + cx + "&q=" + search_content
              axios.get(googleURL)
              .then(response => console.log(response));

            }
          });
          },
          content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
        }}
      />
      <div>
        <p>{cardContent}</p>
      </div>
    </>
  );
}
